SELECT payment_status, COUNT(1) as count, COALESCE(SUM(payment_amount), 0) as total_amount, ROUND(COUNT(1) * 100.0 / SUM(COUNT(1)) OVER(), 2) as percentage FROM payment_records WHERE deleted = 0
AND payment_type = #{paymentType}
AND create_time >= #{startTime}
AND create_time <= #{endTime}
GROUP BY payment_status ORDER BY count DESC

SELECT COALESCE(SUM(payment_amount), 0)
FROM payment_records
WHERE deleted = 0
AND user_id = #{userId}

    AND payment_status = #{paymentStatus}


    AND payment_time >= #{startTime}


    AND payment_time <= #{endTime}

SELECT COUNT(1) as total_count, COALESCE(SUM(CASE WHEN payment_status = 'SUCCESS' THEN payment_amount ELSE 0 END), 0) as success_amount, COALESCE(SUM(CASE WHEN payment_status = 'SUCCESS' THEN 1 ELSE 0 END), 0) as success_count, COALESCE(SUM(CASE WHEN payment_status = 'FAILED' THEN 1 ELSE 0 END), 0) as failed_count, COALESCE(SUM(CASE WHEN payment_status = 'REFUNDED' THEN refund_amount ELSE 0 END), 0) as refund_amount, ROUND( CASE WHEN COUNT(1) > 0 THEN SUM(CASE WHEN payment_status = 'SUCCESS' THEN 1 ELSE 0 END) * 100.0 / COUNT(1) ELSE 0 END, 2 ) as success_rate FROM payment_records WHERE deleted = 0
AND create_time >= #{startTime}
AND create_time <= #{endTime}
